eventscript:
if (message().feature=='rfids') {
    var cmd = {'feature':'indicators', 'cmd':'indicate', 'effect':'pulse', 'dx':15 };
    setfield(cmd, 'x', message().reader*15);
    var id = '0x' + message().nUID;
    var num = cyclic(number(id), 0, 360);
    setfield(cmd, 'color', hsv(num));
    log(5,cmd);
    call(cmd);
